async function grok_image_generation(parameters = {}, settings = {}) {
  const prompt = parameters.prompt || '';
  const apiKey = settings.xaiApiKey || '';

  if (!apiKey) {
    return 'Error: x.ai API key is required. Set it in plugin settings.';
  }

  if (!prompt) {
    return 'Error: Prompt is required but was not provided in parameters.';
  }

  try {
    const response = await fetch('https://api.x.ai/v1/images/generations', {
      method: 'POST',
      headers: {
        'accept': 'application/json',
        'Authorization': `Bearer ${apiKey}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        prompt: prompt,
        model: 'grok-2-image'
      }),
    });

    if (!response.ok) {
      throw new Error(`API error: ${response.status} - ${await response.text()}`);
    }

    const data = await response.json();
    const imageUrl = data.data[0].url;

    return `![Generated Image](${imageUrl})\n\nPrompt: ${prompt}`;
  } catch (error) {
    return `Error: Failed to generate image: ${error.message}`;
  }
};
